mapper.select:
SELECT EXISTS(
SELECT 1 FROM boardLikesUser
WHERE boardNo = #{boardNo} AND userNo = #{userNo}
)

SELECT
c.commentNo, c.boardNo, c.userNo, c.writtenTime, c.content, u.userNick, u.userExp
FROM comment c LEFT JOIN user u ON c.userNo = u.userNo
WHERE c.boardNo = #{boardNo}
LIMIT #{searchPageDto.size}
OFFSET #{searchPageDto.offset}

SELECT count(*)
FROM comment c
WHERE c.boardNo = #{boardNo}

select likeCheck
from boardLikesUser
where boardNo = #{boardNo} and userNo = #{userNo}

select boardTitle, writtenTime from board
where userNo = #{userNo}
limit  #{searchPageDto.size}
offset #{searchPageDto.offset}

SELECT userNo
FROM user
WHERE userId = #{userId}

SELECT count(*)
FROM myCourse
WHERE userNo = #{userNo}

mapper.update:
UPDATE user
SET userExp = userExp +3
WHERE userNo = #{userNo}

UPDATE user
SET userExp = userExp +1
WHERE userNo = #{userNo}
