mapper.update:
update reportedContent
set censoredTime = #{localDateTime}
where userNo = #{userNo}

update reportedContent
set censoredTime = null
where userNo = #{userNo}

update reportedContent
set censoredTime = #{localDateTime}
where reportedContentNo = #{reportedContentNo}

update reportedContent
set censoredTime = null
where reportedContentNo = #{reportedContentNo}
