SELECT m.userNo, m.placeNo, p.placeTitle, p.cat1, p.cat3, p.firstImage, p.firstImage2, p.mapx, p.mapy
FROM myFavoritesUserPlace m JOIN place p ON m.placeNo = p.placeNo
WHERE p.cat1 = 'A03';

SELECT m.userNo, m.placeNo, p.placeTitle, p.cat1, p.cat3, p.firstImage, p.firstImage2, p.mapx, p.mapy
FROM myFavoritesUserPlace m JOIN place p ON m.placeNo = p.placeNo
WHERE p.cat1 = 'A05' AND p.cat3 != 'A05020900';

SELECT m.userNo, m.placeNo, p.placeTitle, p.cat1, p.cat3, p.firstImage, p.firstImage2, p.mapx, p.mapy
FROM myFavoritesUserPlace m JOIN place p ON m.placeNo = p.placeNo
WHERE p.cat1 = 'A05' AND p.cat3 = 'A05020900';

SELECT m.userNo, m.placeNo, p.placeTitle, p.cat1, p.cat3, p.firstImage, p.firstImage2, p.mapx, p.mapy
FROM myFavoritesUserPlace m JOIN place p ON m.placeNo = p.placeNo
WHERE p.cat1 = 'A02';

SELECT m.userNo, m.placeNo, p.placeTitle, p.cat1, p.cat3, p.firstImage, p.firstImage2, p.mapx, p.mapy
FROM myFavoritesUserPlace m JOIN place p ON m.placeNo = p.placeNo
WHERE p.cat1 = 'B02';

SELECT userNo
FROM user
WHERE userId = #{userId}
